import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import L from "leaflet";
import { useSpeechRecognition, SpeechRecognition } from "react-speech-recognition";
import "./RoutePlanning.css";

// Fix Leaflet default icon URLs
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png")
});

// Load hazards.json (ensure it exists in /public/)
async function loadHazards() {
  const response = await fetch("/hazards.json");
  if (!response.ok) throw new Error("Failed to load hazards");
  return await response.json();
}

// Calculate distance between two GPS points
function getDistanceMeters(lat1, lon1, lat2, lon2) {
  const R = 6371000; // meters
  const dLat = ((lat2 - lat1) * Math.PI) / 180;
  const dLon = ((lon2 - lon1) * Math.PI) / 180;
  const a =
    Math.sin(dLat / 2) ** 2 +
    Math.cos((lat1 * Math.PI) / 180) *
      Math.cos((lat2 * Math.PI) / 180) *
      Math.sin(dLon / 2) ** 2;
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
}

// Find hazards near route
function checkHazardsNearRoute(routeCoords, hazards) {
  const result = [];
  for (const hazard of hazards) {
    for (const [lat, lon] of routeCoords) {
      if (getDistanceMeters(lat, lon, hazard.lat, hazard.lon) <= hazard.radius_meters) {
        result.push(hazard);
        break; // stop checking this hazard after first match
      }
    }
  }
  return result;
}

// Geocode place name to coords
async function getCoordinates(place) {
  const response = await fetch(
    `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(place)}&limit=1`
  );
  const data = await response.json();
  if (data && data.length > 0) {
    return [parseFloat(data[0].lat), parseFloat(data[0].lon)];
  }
  throw new Error("Location not found: " + place);
}

// Use OSRM API to fetch route info
async function getRoute(startCoords, endCoords) {
  const response = await fetch(
    `https://router.project-osrm.org/route/v1/driving/${startCoords[1]},${startCoords[0]};${endCoords[1]},${endCoords[0]}?overview=full&geometries=geojson&steps=true`
  );
  const data = await response.json();
  if (data.routes && data.routes.length > 0) {
    const route = data.routes[0];
    const steps = route.legs.flatMap(leg => leg.steps.map(step => step.maneuver.instruction));
    return {
      coordinates: route.geometry.coordinates.map(([lng, lat]) => [lat, lng]),
      distance: (route.distance / 1000).toFixed(2),
      duration: (route.duration / 60).toFixed(0),
      steps,
    };
  }
  throw new Error("Route data unavailable");
}

// Main component
export default function RoutePlanning() {
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [routeCoords, setRouteCoords] = useState(null);
  const [routeInfo, setRouteInfo] = useState(null);
  const [hazards, setHazards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [routeFound, setRouteFound] = useState(false);
  const [recentRoutes, setRecentRoutes] = useState([]);
  const [routeSteps, setRouteSteps] = useState([]);

