  const [weather, setWeather] = useState({ temperature: "32¬∞C", condition: "Clear", rain: "10%", wind: "6 km/h" });
  const [traffic, setTraffic] = useState({ status: "Smooth traffic", congestion: "Medium" });
  const [safePlaces, setSafePlaces] = useState([
    { name: "Apollo Hospital", distance: "2.1 km", icon: "üè•" },
    { name: "Police Station", distance: "1.4 km", icon: "üöì" },
    { name: "Petrol Pump", distance: "0.9 km", icon: "‚õΩ" }
  ]);

  // Load recent routes from localStorage on mount
  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("recentRoutes"));
    if (stored && Array.isArray(stored)) setRecentRoutes(stored);
