      const match = cmd.match(/safe route from (.+) to (.+)/);
      if (match && match.length === 3) {
        const fromCity = match[1].trim();
        const toCity = match[2].trim();
        setFrom(fromCity);
        setTo(toCity);
        await handleFindRoute(fromCity, toCity);
        return;
      }
    } else if (cmd.includes("start navigation")) {
      handleStartNavigation();
      return;
    }
    alert("Command not recognized. Please try again.");
  }

  // Main route fetch
  async function handleFindRoute() {
    if (!from || !to) {
      alert("Please enter both From and To locations.");
      return;
    }
    setLoading(true);
    try {
      // Append ', India' for reliable geocoding
      const fromCoords = await getCoordinates(from.trim() + ", India");
      const toCoords = await getCoordinates(to.trim() + ", India");
      const route = await getRoute(fromCoords, toCoords);

      setRouteCoords(route.coordinates);
      setRouteInfo(route);
      setRouteSteps(route.steps || []);

      const hazardsData = await loadHazards();
      const hazardsOnRoute = checkHazardsNearRoute(route.coordinates, hazardsData);
