      if (hazardsOnRoute.length === 0) {
        speak("No major hazards detected. Safe route.");
      } else {
        const msgs = hazardsOnRoute.map(h => `${h.description} at ${h.name}`);
        speak("Attention: " + msgs.join(". "));
      }
