              placeholder="Enter destination"
            />
          </div>
          <button onClick={handleFindRoute} disabled={loading}>
            {loading ? "Finding Safe Route..." : "Find Safe Route"}
          </button>

          {routeFound && routeInfo && (
            <>
              <div className="route-info">
                <h3>‚úÖ Safe Route Found</h3>
                <div>
                  <strong>Distance:</strong> {routeInfo.distance} km
                </div>
                <div>
                  <strong>Estimated Time:</strong> {Math.floor(routeInfo.duration)} min
                </div>
              </div>

              <div className="route-warnings">
                <h4>‚ö†Ô∏è Safety Alerts</h4>
                {hazards.length === 0 ? (
                  <div className="alert success">‚úÖ No hazards detected</div>
