import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import L from "leaflet";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";
import "./RoutePlanning.css";

// Fix Leaflet default icon issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
Â  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
Â  iconUrl: require("leaflet/dist/images/marker-icon.png"),
Â  shadowUrl: require("leaflet/dist/images/marker-shadow.png")
});

async function loadHazards() {
Â  const response = await fetch("/hazards.json");
Â  if (!response.ok) throw new Error("Failed to load hazards data");
Â  return await response.json();
}

function getDistanceMeters(lat1, lon1, lat2, lon2) {
Â  const toRad = (x) => (x * Math.PI) / 180;
Â  const R = 6371000;
Â  const dLat = toRad(lat2 - lat1);
Â  const dLon = toRad(lon2 - lon1);
Â  const a =
Â  Â  Math.sin(dLat / 2) * Math.sin(dLat / 2) +
Â  Â  Math.cos(toRad(lat1)) *
Â  Â  Â  Math.cos(toRad(lat2)) *
Â  Â  Â  Math.sin(dLon / 2) *
Â  Â  Â  Math.sin(dLon / 2);
Â  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
Â  return R * c;
}

function checkHazardsNearRoute(routeCoords, hazards) {
Â  const result = [];
Â  hazards.forEach((hazard) => {
Â  Â  for (const [lat, lon] of routeCoords) {
Â  Â  Â  if (getDistanceMeters(lat, lon, hazard.lat, hazard.lon) <= hazard.radius_meters) {
Â  Â  Â  Â  result.push(hazard);
Â  Â  Â  Â  break;
Â  Â  Â  }
Â  Â  }
Â  });
Â  return result;
}

async function getCoordinates(place) {
Â  const response = await fetch(
Â  Â  `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(place)}&limit=1`
Â  );
Â  const data = await response.json();
Â  if (data && data.length > 0) {
Â  Â  return [parseFloat(data[0].lat), parseFloat(data[0].lon)];
Â  }
Â  throw new Error("Location not found: " + place);
}

async function getRoute(startCoords, endCoords) {
Â  const response = await fetch(
Â  Â  `https://router.project-osrm.org/route/v1/driving/${startCoords[1]},${startCoords[0]};${endCoords[1]},${endCoords[0]}?overview=full&geometries=geojson&steps=true`
Â  );
Â  const data = await response.json();
Â  if (data.routes && data.routes.length > 0) {
Â  Â  const route = data.routes[0];
Â  Â  const steps = route.legs.flatMap(leg => leg.steps.map(step => step.maneuver.instruction));
Â  Â  return {
Â  Â  Â  coordinates: route.geometry.coordinates.map(([lng, lat]) => [lat, lng]),
Â  Â  Â  distance: route.distance / 1000,
Â  Â  Â  duration: route.duration / 60,
Â  Â  Â  steps,
Â  Â  };
Â  }
Â  throw new Error("Route calculation failed");
}

function VoiceChatbot({ onCommand }) {
Â  const { transcript, listening, resetTranscript, browserSupportsSpeechRecognition } = useSpeechRecognition();

Â  useEffect(() => {
Â  Â  if (!listening && transcript.trim()) {
Â  Â  Â  onCommand(transcript.trim());
Â  Â  Â  resetTranscript();
Â  Â  }
Â  }, [listening, transcript, onCommand, resetTranscript]);

Â  if (!browserSupportsSpeechRecognition) {
Â  Â  return <span>Your browser does not support speech recognition.</span>;
Â  }

Â  return (
Â  Â  <div style={{ background: "#fff6", borderRadius: 8, padding: 12, marginBottom: 20 }}>
Â  Â  Â  <button
Â  Â  Â  Â  onClick={() => SpeechRecognition.startListening({ continuous: false, language: "en-IN" })}
Â  Â  Â  Â  disabled={listening}
Â  Â  Â  Â  style={{
Â  Â  Â  Â  Â  fontWeight: 700,
Â  Â  Â  Â  Â  fontSize: 16,
Â  Â  Â  Â  Â  padding: "8px 16px",
Â  Â  Â  Â  Â  borderRadius: 6,
Â  Â  Â  Â  Â  background: "#3f72af",
Â  Â  Â  Â  Â  color: "#fff",
Â  Â  Â  Â  Â  cursor: listening ? "default" : "pointer"
Â  Â  Â  Â  }}
Â  Â  Â  >
Â  Â  Â  Â  ğŸ¤ {listening ? "Listening..." : "Start Voice"}
Â  Â  Â  </button>
Â  Â  Â  <span style={{ marginLeft: 12, fontStyle: "italic" }}>
Â  Â  Â  Â  {listening ? "Speak your command..." : transcript ? `Heard: "${transcript}"` : ""}
Â  Â  Â  </span>
Â  Â  </div>
Â  );
}

export default function RoutePlanning() {
Â  const [fromLocation, setFromLocation] = useState("");
Â  const [toLocation, setToLocation] = useState("");
Â  const [routeCoords, setRouteCoords] = useState(null);
Â  const [routeInfo, setRouteInfo] = useState(null);
Â  const [hazards, setHazards] = useState([]);
Â  const [loading, setLoading] = useState(false);
Â  const [routeFound, setRouteFound] = useState(false);
Â  const [feedback, setFeedback] = useState("");
Â  const [recentRoutes, setRecentRoutes] = useState([]);
Â  const [routeSteps, setRouteSteps] = useState([]);

Â  // Mock weather, traffic, safe places (static for demo)
Â  const [weather, setWeather] = useState({
Â  Â  temperature: "32Â°C",
Â  Â  condition: "Clear (Sample)",
Â  Â  rain: "10%",
Â  Â  wind: "6 km/h",
Â  });
Â  const [traffic, setTraffic] = useState({ status: "Smooth traffic", congestion: "Medium" });
Â  const [safePlaces, setSafePlaces] = useState([
Â  Â  { name: "Apollo Hospital", distance: "2.1 km", icon: "ğŸ¥" },
Â  Â  { name: "Police Station", distance: "1.4 km", icon: "ğŸš“" },
Â  Â  { name: "Petrol Pump", distance: "0.9 km", icon: "â›½" },
Â  ]);

Â  // Load recent routes from localStorage
Â  useEffect(() => {
Â  Â  const stored = JSON.parse(localStorage.getItem("recentRoutes"));
Â  Â  if (stored && Array.isArray(stored)) {
Â  Â  Â  setRecentRoutes(stored);
Â  Â  }
Â  }, []);

Â  useEffect(() => {
Â  Â  localStorage.setItem("recentRoutes", JSON.stringify(recentRoutes));
Â  }, [recentRoutes]);

Â  function speak(message) {
Â  Â  if ("speechSynthesis" in window) {
Â  Â  Â  window.speechSynthesis.cancel();
Â  Â  Â  const utter = new SpeechSynthesisUtterance(message);
Â  Â  Â  utter.lang = "en-IN";
Â  Â  Â  window.speechSynthesis.speak(utter);
Â  Â  }
Â  }

Â  async function handleVoiceCommand(text) {
Â  Â  const lowerText = text.toLowerCase();

Â  Â  if (lowerText.includes("safe route from")) {
Â  Â  Â  const parts = lowerText.match(/safe route from (.+) to (.+)/);
Â  Â  Â  if (parts && parts.length === 3) {
Â  Â  Â  Â  const from = parts[1].trim();
Â  Â  Â  Â  const to = parts[2].trim();
Â  Â  Â  Â  setFromLocation(from);
Â  Â  Â  Â  setToLocation(to);
Â  Â  Â  Â  speak(`Finding the safest route from ${from} to ${to}`);
Â  Â  Â  Â  await handleFindRoute(from, to);
Â  Â  Â  Â  return;
Â  Â  Â  }
Â  Â  } else if (lowerText.includes("start navigation")) {
Â  Â  Â  speak("Starting navigation in Google Maps");
Â  Â  Â  handleStartNavigation();
Â  Â  Â  return;
Â  Â  }
Â  Â  speak("Sorry, I did not understand that. Please try again.");
Â  }

Â  async function handleFindRoute(inputFrom, inputTo) {
Â  Â  const from = inputFrom || fromLocation;
Â  Â  const to = inputTo || toLocation;

Â  Â  if (!from || !to) {
Â  Â  Â  alert("Please enter both source and destination.");
Â  Â  Â  return;
Â  Â  }
Â  Â  setLoading(true);
Â  Â  setRouteCoords(null);
Â  Â  setRouteInfo(null);
Â  Â  setHazards([]);
Â  Â  setRouteFound(false);
Â  Â  setRouteSteps([]);

Â  Â  try {
Â  Â  Â  const fromCoords = await getCoordinates(from.trim());
Â  Â  Â  const toCoords = await getCoordinates(to.trim());
Â  Â  Â  const route = await getRoute(fromCoords, toCoords);

Â  Â  Â  setRouteCoords(route.coordinates);
Â  Â  Â  setRouteInfo(route);
Â  Â  Â  setRouteSteps(route.steps || []);

Â  Â  Â  const hazardsData = await loadHazards();
Â  Â  Â  const hazardsOnRoute = checkHazardsNearRoute(route.coordinates, hazardsData);
Â  Â  Â  setHazards(hazardsOnRoute);

Â  Â  Â  setFromLocation(from);
Â  Â  Â  setToLocation(to);

Â  Â  Â  setRouteFound(true);

Â  Â  Â  // Save route to recentRoutes if not duplicate
Â  Â  Â  setRecentRoutes(prev =>
Â  Â  Â  Â  prev.find(r => r.from === from && r.to === to)
Â  Â  Â  Â  Â  ? prev
Â  Â  Â  Â  Â  : [...prev, { from, to }]
Â  Â  Â  );

Â  Â  Â  if (hazardsOnRoute.length === 0) {
Â  Â  Â  Â  speak("No major hazards detected. Safe route.");
Â  Â  Â  } else {
Â  Â  Â  Â  const messages = hazardsOnRoute.map(hz => `${hz.description} at ${hz.name}`);
Â  Â  Â  Â  speak("Attention: " + messages.join(". "));
Â  Â  Â  }
Â  Â  } catch (err) {
Â  Â  Â  alert("Error: " + err.message);
Â  Â  }
Â  Â  setLoading(false);
Â  }

Â  function handleStartNavigation() {
Â  Â  if (routeCoords && routeCoords.length >= 2) {
Â  Â  Â  const start = routeCoords[0];
Â  Â  Â  const end = routeCoords[routeCoords.length - 1];
Â  Â  Â  // Use lat,lon in correct order for Google Maps URL (lat,lng)
Â  Â  Â  const url = `https://www.google.com/maps/dir/?api=1&origin=${start[0]},${start[1]}&destination=${end[0]},${end[1]}`;
Â  Â  Â  window.open(url, "_blank");
Â  Â  }
Â  }

Â  function handleSubmitFeedback() {
Â  Â  if (feedback.trim()) {
Â  Â  Â  alert("Thank you for your feedback!");
Â  Â  Â  setFeedback("");
Â  Â  } else {
Â  Â  Â  alert("Please enter your feedback before submitting.");
Â  Â  }
Â  }

Â  function fillRecentRoute(from, to) {
Â  Â  setFromLocation(from);
Â  Â  setToLocation(to);
Â  Â  setRouteFound(false);
Â  }

Â  const cardStyle = {
Â  Â  background: "#f6f9ff",
Â  Â  borderRadius: "12px",
Â  Â  boxShadow: "0 2px 8px #e4e9fa50",
Â  Â  padding: "18px 20px",
Â  Â  minWidth: "220px",
Â  Â  flex: "1 1 220px",
Â  Â  color: "#333"
Â  };

Â  return (
Â  Â  <div className="route-planning-container">
Â  Â  Â  <div className="planning-panel">
Â  Â  Â  Â  <h1>SafePath AI</h1>

Â  Â  Â  Â  <VoiceChatbot onCommand={handleVoiceCommand} />

Â  Â  Â  Â  <div className="route-form">
Â  Â  Â  Â  Â  <h2>ğŸ—ºï¸ Route Planning</h2>
Â  Â  Â  Â  Â  <div className="input-group">
Â  Â  Â  Â  Â  Â  <label>From</label>
Â  Â  Â  Â  Â  Â  <input
Â  Â  Â  Â  Â  Â  Â  type="text"
