import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import './RoutePlanning.css';

// Fix for leaflet icons
import L from 'leaflet';
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const RoutePlanning = () => {
  const [fromLocation, setFromLocation] = useState('');
  const [toLocation, setToLocation] = useState('');
  const [route, setRoute] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [routeFound, setRouteFound] = useState(false);

  // Sample route coordinates for demo (Chennai to Salem area)
  const sampleRoute = [
    [13.0827, 80.2707], // Chennai
    [13.1000, 80.1000],
    [13.2000, 79.8000],
    [13.3000, 79.6000],
    [11.6643, 78.1460]  // Salem
  ];

  const handleFindRoute = () => {
    if (!fromLocation || !toLocation) {
      alert('Please enter both From and To locations');
      return;
    }

    setIsLoading(true);
    
    // Simulate API call delay
    setTimeout(() => {
      setRoute(sampleRoute);
      setRouteFound(true);
      setIsLoading(false);
    }, 2000);
  };

  const handleCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        setFromLocation(`Current Location (${position.coords.latitude.toFixed(4)}, ${position.coords.longitude.toFixed(4)})`);
      });
    } else {
      setFromLocation('Chennai, Tamil Nadu');
    }
  };

  return (
    <div className="route-planning-container">
      {/* Left Panel - Route Planning Form */}
      <div className="planning-panel">
        <div className="header">
          <h1>SafePath AI</h1>
        </div>

        <div className="route-form">
          <h2>üó∫Ô∏è Route Planning</h2>
          
          <div className="input-group">
            <label>From</label>
            <div className="input-container">
              <input
                type="text"
                value={fromLocation}
                onChange={(e) => setFromLocation(e.target.value)}
                placeholder="Enter starting location"
                className="location-input"
              />
              <button 
                onClick={handleCurrentLocation}
                className="current-location-btn"
                title="Use current location"
              >
                üìç
              </button>
            </div>
          </div>

          <div className="input-group">
            <label>To</label>
            <input
              type="text"
              value={toLocation}
              onChange={(e) => setToLocation(e.target.value)}
              placeholder="Enter destination"
              className="location-input"
            />
          </div>

          <button 
            onClick={handleFindRoute}
            className="find-route-btn"
            disabled={isLoading}
          >
            {isLoading ? 'üîÑ Finding Safe Route...' : 'üõ°Ô∏è Find Safe Route'}
          </button>

          {routeFound && (
            <div className="route-info">
              <div className="route-stats">
                <h3>‚úÖ Safe Route Found</h3>
                <div className="stats">
                  <div className="stat-item">
                    <span className="label">Distance:</span>
                    <span className="value">347 km</span>
                  </div>
                  <div className="stat-item">
                    <span className="label">Estimated Time:</span>
                    <span className="value">4h 25m</span>
                  </div>
                  <div className="stat-item">
                    <span className="label">Safety Score:</span>
                    <span className="value safe">92%</span>
                  </div>
                  <div className="stat-item">
                    <span className="label">Hazards Avoided:</span>
                    <span className="value">3</span>
                  </div>
                </div>
              </div>

              <div className="route-warnings">
                <h4>‚ö†Ô∏è Safety Alerts</h4>
                <div className="alert-item warning">
                  <span>üöß</span> Construction work near Vellore (15 min delay)
                </div>
                <div className="alert-item info">
                  <span>üåßÔ∏è</span> Light rain expected after 6 PM
                </div>
                <div className="alert-item success">
                  <span>‚úÖ</span> Alternative route suggested - 12 min faster
                </div>
              </div>

              <button className="start-navigation-btn">
                üöó Start Navigation
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Right Panel - Map */}
      <div className="map-panel">
        <MapContainer 
          center={[12.9250, 80.2250]} 
          zoom={8} 
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer
            attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          
          {/* Start Marker */}
          {routeFound && (
            <Marker position={[13.0827, 80.2707]}>
              <Popup>Starting Point: {fromLocation}</Popup>
            </Marker>
          )}
          
          {/* End Marker */}
          {routeFound && (
            <Marker position={[11.6643, 78.1460]}>
              <Popup>Destination: {toLocation}</Popup>
            </Marker>
          )}
          
          {/* Route Line */}
          {route && (
            <Polyline 
              positions={route} 
              color="#4CAF50" 
              weight={6}
              opacity={0.8}
            />
          )}
        </MapContainer>
      </div>
    </div>
  );
};

export default RoutePlanning;
