import React, { useState } from "react";
import {
  MapContainer,
  TileLayer,
  Marker,
  Popup,
  Polyline,
} from "react-leaflet";
import L from "leaflet";
import "./RoutePlanning.css";

// Fix for leaflet icon assets
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png"),
});

// Get latitude and longitude for a place name
async function getCoordinates(place) {
  const response = await fetch(
    `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
      place
    )}&limit=1`
  );
  const data = await response.json();
  if (data && data.length > 0) {
    return [parseFloat(data[0].lat), parseFloat(data[0].lon)];
  }
  throw new Error("Location not found: " + place);
}

// Get route between two points using OSRM
async function getRoute(startCoords, endCoords) {
  const response = await fetch(
    `https://router.project-osrm.org/route/v1/driving/${startCoords[1]},${startCoords[0]};${endCoords[1]},${endCoords[0]}?overview=full&geometries=geojson`
  );
  const data = await response.json();
  if (data && data.routes && data.routes.length > 0) {
    const route = data.routes[0];
    return {
      coordinates: route.geometry.coordinates.map(([lng, lat]) => [
        lat,
        lng,
      ]),
      distance: route.distance / 1000, // in km
      duration: route.duration / 60, // in minutes
    };
  }
  throw new Error("Route calculation failed");
}

// Fetch weather alerts from OpenWeatherMap API
async function fetchWeatherAlerts(lat, lon, apiKey) {
  const response = await fetch(
    `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&exclude=current,minutely,hourly,daily&appid=${apiKey}`
  );
  const data = await response.json();
  if (data && data.alerts && data.alerts.length > 0) {
    return data.alerts.map((alert) => ({
      type: "warning",
      icon: "üåßÔ∏è",
      text: `${alert.event}: ${alert.description}`,
    }));
  }
  return [];
}

// Mocked traffic alerts (replace with real API calls)
async function fetchTrafficAlerts() {
  return [
    {
      type: "danger",
      icon: "üöß",
      text: "Construction work causing slowdowns on main highway.",
    },
    {
      type: "danger",
      icon: "‚ö†Ô∏è",
      text: "Recent accident reported near your route, proceed with caution.",
    },
  ];
}

export default function RoutePlanning() {
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [routeCoords, setRouteCoords] = useState(null);
  const [routeInfo, setRouteInfo] = useState(null);
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(false);

  // Add your actual OpenWeatherMap API key here
  const OPENWEATHERMAP_API_KEY = "YOUR_OPENWEATHERMAP_API_KEY";

  async function handleFindRoute() {
    if (!from || !to) {
      alert("Please enter both source and destination.");
      return;
    }
    setLoading(true);
    setAlerts([]);
    setRouteCoords(null);
    setRouteInfo(null);

    try {
      const fromCoords = await getCoordinates(from.trim());
      const toCoords = await getCoordinates(to.trim());

      const route = await getRoute(fromCoords, toCoords);

      setRouteCoords(route.coordinates);
      setRouteInfo(route);

      // Fetch alerts
      const weatherAlerts = await fetchWeatherAlerts(
        fromCoords[0],
        fromCoords[1],
        OPENWEATHERMAP_API_KEY
      );
      const trafficAlerts = await fetchTrafficAlerts(); // Replace with real call

      setAlerts([...weatherAlerts, ...trafficAlerts]);
    } catch (error) {
      alert("Error: " + error.message);
    }

    setLoading(false);
  }

  function handleStartNavigation() {
    if (routeCoords && routeCoords.length >= 2) {
      const start = routeCoords[0];
      const end = routeCoords[routeCoords.length - 1];
      const url = `https://www.google.com/maps/dir/?api=1&origin=${start[0]},${start[1]}&destination=${end[0]},${end[1]}`;
      window.open(url, "_blank");
    }
  }

  return (
    <div className="route-planning-container">
      <div className="planning-panel">
        <h1>SafePath AI</h1>
        <div className="route-form">
          <h2>üó∫Ô∏è Route Planning</h2>

          <div className="input-group">
            <label>From</label>
            <input
              value={from}
              onChange={(e) => setFrom(e.target.value)}
              placeholder="Enter starting location"
              className="location-input"
              type="text"
            />
          </div>

          <div className="input-group">
            <label>To</label>
            <input
              value={to}
              onChange={(e) => setTo(e.target.value)}
              placeholder="Enter destination"
              className="location-input"
              type="text"
            />
          </div>

          <button
            onClick={handleFindRoute}
            disabled={loading}
            className="find-route-btn"
            type="button"
          >
            {loading ? "Loading..." : "Find Safe Route"}
          </button>

          {routeInfo && (
            <div className="route-info">
              <h3>‚úÖ Safe Route Found</h3>
              <div>Distance: {routeInfo.distance.toFixed(1)} km</div>
              <div>
                Estimated Time:{" "}
                {Math.floor(routeInfo.duration / 60)}h {Math.round(routeInfo.duration % 60)}m
              </div>
              <button onClick={handleStartNavigation} className="start-button">
                Start Navigation
              </button>
            </div>
          )}

          <div className="alerts-container">
            <h4>Alerts:</h4>
            {alerts.length === 0 && <div>No alerts detected.</div>}
            {alerts.map((alert, idx) => (
              <div key={idx} className={`alert-item ${alert.type}`}>
                <span>{alert.icon}</span> {alert.text}
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="map-container">
        <MapContainer
          center={routeCoords ? routeCoords[0] : [13.0827, 80.2707]}
          zoom={7}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="¬© OpenStreetMap contributors"
          />
          {routeCoords && (
            <>
              <Marker position={routeCoords[0]}>
                <Popup>Start: {from}</Popup>
              </Marker>
              <Marker position={routeCoords[routeCoords.length - 1]}>
                <Popup>End: {to}</Popup>
              </Marker>
              <Polyline positions={routeCoords} color="green" />
            </>
          )}
        </MapContainer>
      </div>
    </div>
  );
}
