import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline, useMap } from 'react-leaflet';
import './RoutePlanning.css';

// Fix for leaflet icons
import L from 'leaflet';
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Geocoding function
const fetchCoordinates = async (location) => {
  const url = `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(location)}`;
  const response = await fetch(url, { 
    headers: { 'Accept': 'application/json' } 
  });
  const data = await response.json();
  if (data.length > 0) {
    return [parseFloat(data.lat), parseFloat(data.lon)];
  } else {
    throw new Error(`Location "${location}" not found.`);
  }
};

// Routing function using OSRM
const fetchRoute = async (startCoords, endCoords) => {
  try {
    const url = `https://router.project-osrm.org/route/v1/driving/${startCoords[1]},${startCoords};${endCoords[1]},${endCoords}?overview=full&geometries=geojson`;
    
    const response = await fetch(url);
    
    if (!response.ok) {
      throw new Error('Route could not be calculated');
    }
    
    const data = await response.json();
    
    if (data.routes && data.routes.length > 0) {
      const route = data.routes;
      const coordinates = route.geometry.coordinates;
      
      // Convert [lng, lat] to [lat, lng] for Leaflet
      const leafletCoords = coordinates.map(coord => [coord[1], coord]);
      
      return {
        coordinates: leafletCoords,
        distance: Math.round(route.distance / 10) / 100, // Convert to km with 2 decimals
        duration: Math.round(route.duration / 60), // Convert to minutes
      };
    } else {
      throw new Error('No route found');
    }
  } catch (error) {
    console.warn('OSRM routing failed, using straight line:', error);
    const distance = Math.round(
      Math.sqrt(
        Math.pow((startCoords - endCoords) * 111, 2) +
        Math.pow((startCoords[1] - endCoords[1]) * 111, 2)
      ) * 100
    ) / 100;
    
    return {
      coordinates: [startCoords, endCoords],
      distance: distance,
      duration: Math.round(distance / 50 * 60), // Assume 50 km/h average speed
    };
  }
};

// Component to update map view
const ChangeMapView = ({ center, zoom }) => {
  const map = useMap();
  React.useEffect(() => {
    map.setView(center, zoom);
  }, [center, zoom, map]);
  return null;
};

const RoutePlanning = () => {
  const [fromLocation, setFromLocation] = useState('');
  const [toLocation, setToLocation] = useState('');
  const [route, setRoute] = useState(null);
  const [routeData, setRouteData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [routeFound, setRouteFound] = useState(false);
  const [mapCenter, setMapCenter] = useState([12.9250, 80.2250]);
  const [mapZoom, setMapZoom] = useState(7);
  const [routeKey, setRouteKey] = useState(0);

  // MAIN ROUTE FINDING FUNCTION - FIXED
  const handleFindRoute = async () => {
    if (!fromLocation.trim() || !toLocation.trim()) {
      alert('Please enter both From and To locations');
      return;
    }
    
    console.log(`üîç Finding route from "${fromLocation}" to "${toLocation}"`);
    setIsLoading(true);
    
    // Clear everything first
    setRoute(null);
    setRouteData(null);
    setRouteFound(false);
    setRouteKey(prev => prev + 1);
    
    try {
      // Step 1: Geocode both locations
      console.log('üåç Geocoding locations...');
      const fromCoords = await fetchCoordinates(fromLocation.trim());
      const toCoords = await fetchCoordinates(toLocation.trim());
      
      console.log('üìç From:', fromCoords, 'To:', toCoords);
      
      // Step 2: Get the route
      console.log('üõ£Ô∏è Fetching route...');
      const routeInfo = await fetchRoute(fromCoords, toCoords);
      
      console.log('‚úÖ Route found:', {
        distance: routeInfo.distance + 'km',
        duration: routeInfo.duration + 'min',
        points: routeInfo.coordinates.length
      });
      
      // Step 3: Update all states
      setRoute(routeInfo.coordinates);
      setRouteData(routeInfo);
      setRouteFound(true);
      setMapCenter(fromCoords);
      setMapZoom(8);
      
    } catch (error) {
      console.error('‚ùå Route error:', error);
      alert(`Error: ${error.message}`);
      setRouteFound(false);
      setRoute(null);
      setRouteData(null);
    }
    
    setIsLoading(false);
  };

  const handleCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        setFromLocation(
          `${position.coords.latitude.toFixed(6)}, ${position.coords.longitude.toFixed(6)}`
        );
      });
    } else {
      alert('Geolocation is not supported by this browser');
    }
  };

  const handleStartNavigation = () => {
    if (route && route.length >= 2) {
      const start = route;
      const end = route[route.length - 1];
      const gmapsUrl = `https://www.google.com/maps/dir/?api=1&origin=${start},${start[1]}&destination=${end},${end[1]}`;
      window.open(gmapsUrl, "_blank");
    }
  };

  // Input change handlers that clear route
  const handleFromChange = (e) => {
    setFromLocation(e.target.value);
    setRoute(null);
    setRouteData(null);
    setRouteFound(false);
  };

  const handleToChange = (e) => {
    setToLocation(e.target.value);
    setRoute(null);
    setRouteData(null);
    setRouteFound(false);
  };

  return (
    <div className="route-planning-container">
      {/* Left Panel - Route Planning Form */}
      <div className="planning-panel">
        <div className="header">
          <h1>SafePath AI</h1>
        </div>

        <div className="route-form">
          <h2>üó∫Ô∏è Route Planning</h2>
          
          <div className="input-group">
            <label>From</label>
            <div className="input-container">
              <input
                type="text"
                value={fromLocation}
                onChange={handleFromChange}
                placeholder="Enter starting location"
                className="location-input"
              />
              <button 
                onClick={handleCurrentLocation}
                className="current-location-btn"
                title="Use current location"
                type="button"
              >
                üìç
              </button>
            </div>
          </div>

          <div className="input-group">
            <label>To</label>
            <input
              type="text"
              value={toLocation}
              onChange={handleToChange}
              placeholder="Enter destination"
              className="location-input"
            />
          </div>

          <button 
            onClick={handleFindRoute}
            className="find-route-btn"
            disabled={isLoading}
            type="button"
          >
            {isLoading ? 'üîÑ Finding Safe Route...' : 'üõ°Ô∏è Find Safe Route'}
          </button>

          {routeFound && routeData && (
            <div className="route-info">
              <div className="route-stats">
                <h3>‚úÖ Safe Route Found</h3>
                <div className="stats">
                  <div className="stat-item">
                    <span className="label">Distance:</span>
                    <span className="value">{routeData.distance} km</span>
                  </div>
                  <div className="stat-item">
                    <span className="label">Estimated Time:</span>
                    <span className="value">
                      {Math.floor(routeData.duration / 60)}h {routeData.duration % 60}m
                    </span>
                  </div>
                  <div className="stat-item">
                    <span className="label">Safety Score:</span>
                    <span className="value safe">92%</span>
                  </div>
                  <div className="stat-item">
                    <span className="label">Hazards Avoided:</span>
                    <span className="value">3</span>
                  </div>
                </div>
              </div>

              <div className="route-warnings">
                <h4>‚ö†Ô∏è Safety Alerts</h4>
                <div className="alert-item warning">
                  <span>üöß</span> Construction work possible on main highways
                </div>
                <div className="alert-item info">
                  <span>üåßÔ∏è</span> Check local weather for rain risks
                </div>
                <div className="alert-item success">
                  <span>‚úÖ</span> Route avoids major accident zones
                </div>
              </div>

              <button className="start-navigation-btn" type="button" onClick={handleStartNavigation}>
                üöó Start Navigation
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Right Panel - Map */}
      <div className="map-panel">
        <MapContainer 
          center={mapCenter}
          zoom={mapZoom}
          style={{ height: "100%", width: "100%" }}
          key={`map-${routeKey}`}
        >
          <ChangeMapView center={mapCenter} zoom={mapZoom} />
          <TileLayer
            attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* Start Marker */}
          {route && route.length > 0 && (
            <Marker position={route} key={`start-${routeKey}`}>
              <Popup>Starting Point: {fromLocation}</Popup>
            </Marker>
          )}

          {/* End Marker */}
          {route && route.length > 0 && (
            <Marker position={route[route.length - 1]} key={`end-${routeKey}`}>
              <Popup>Destination: {toLocation}</Popup>
            </Marker>
          )}

          {/* Route Line */}
          {route && route.length > 0 && (
            <Polyline 
              positions={route} 
              color="#4CAF50" 
              weight={6}
              opacity={0.8}
              key={`route-${routeKey}`}
            />
          )}
        </MapContainer>
      </div>
    </div>
  );
};

export default RoutePlanning;
