import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, Polyline, Popup } from 'react-leaflet';
import './RoutePlanning.css';

// Fix for leaflet icons (important!)
import L from 'leaflet';
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Geocoding (address to [lat, lon])
const getCoordinates = async (location) => {
  const response = await fetch(
    `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(location)}&limit=1`
  );
  const data = await response.json();
  if (Array.isArray(data) && data.length > 0 && data[0]) {
    const lat = parseFloat(data[0].lat), lon = parseFloat(data[0].lon);
    if (!isNaN(lat) && !isNaN(lon)) return [lat, lon];
  }
  throw new Error(`Location not found: ${location}`);
};

// Routing (get path between two [lat, lon] points)
const getRoute = async (start, end) => {
  const url = `https://router.project-osrm.org/route/v1/driving/${start[1]},${start[0]};${end[1]},${end[0]}?overview=full&geometries=geojson`;
  const response = await fetch(url);
  const data = await response.json();
  if (data && data.routes && data.routes.length > 0) {
    const r = data.routes[0];
    return {
      coordinates: r.geometry.coordinates.map(([lng, lat]) => [lat, lng]),
      distance: Math.round(r.distance / 100) / 10, // km (1 decimal)
      duration: Math.round(r.duration / 60), // minutes
    };
  }
  throw new Error("Route calculation failed.");
};

const RoutePlanning = () => {
  const [fromLocation, setFromLocation] = useState('');
  const [toLocation, setToLocation] = useState('');
  const [route, setRoute] = useState(null);
  const [routeData, setRouteData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [routeFound, setRouteFound] = useState(false);

  const handleFindRoute = async () => {
    if (!fromLocation || !toLocation) return alert('Please enter both locations.');

    setIsLoading(true);
    setRouteFound(false);
    setRoute(null);
    setRouteData(null);

    try {
      const fromCoords = await getCoordinates(fromLocation.trim());
      const toCoords = await getCoordinates(toLocation.trim());
      const routeInfo = await getRoute(fromCoords, toCoords);
      setRoute(routeInfo.coordinates);
      setRouteData(routeInfo);
      setRouteFound(true);
    } catch (err) {
      alert('Could not find route: ' + err.message);
    }
    setIsLoading(false);
  };

  const handleStartNavigation = () => {
    if (route && route.length >= 2) {
      const start = route[0];
      const end = route[route.length - 1];
      const url = `https://www.google.com/maps/dir/?api=1&origin=${start[0]},${start[1]}&destination=${end[0]},${end[1]}`;
      window.open(url, "_blank");
    }
  };

  return (
    <div className="route-planning-container">
      <div className="planning-panel">
        <div className="header">
          <h1>SafePath AI</h1>
        </div>
        <div className="route-form">
          <h2>🗺️ Route Planning</h2>
          <div className="input-group">
            <label>From</label>
            <input
              type="text"
              value={fromLocation}
              onChange={(e) => setFromLocation(e.target.value)}
              placeholder="Start location (e.g. Chennai)"
              className="location-input"
            />
          </div>
          <div className="input-group">
            <label>To</label>
            <input
              type="text"
              value={toLocation}
              onChange={(e) => setToLocation(e.target.value)}
              placeholder="Destination (e.g. Salem)"
              className="location-input"
            />
          </div>
          <button onClick={handleFindRoute} disabled={isLoading} className="find-route-btn">
            {isLoading ? "🔄 Finding Safe Route..." : "🛡️ Find Safe Route"}
          </button>
          {routeFound && routeData && (
            <div className="route-info">
              <div className="route-stats">
                <h3>✅ Safe Route Found</h3>
                <div className="stats">
                  <div className="stat-item">
                    <span className="label">Distance:</span>
                    <span className="value">{routeData.distance} km</span>
                  </div>
                  <div className="stat-item">
                    <span className="label">Estimated Time:</span>
                    <span className="value">{Math.floor(routeData.duration / 60)}h {routeData.duration % 60}m</span>
                  </div>
                </div>
              </div>
              <button className="start-navigation-btn" type="button" onClick={handleStartNavigation}>
                🚗 Start Navigation
              </button>
            </div>
          )}
        </div>
      </div>
      <div className="map-panel">
        <MapContainer
          center={route && route.length > 0 ? route[0] : [13.0827, 80.2707]}
          zoom={7}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; OpenStreetMap contributors'
          />
          {route && route.length > 0 && (
            <Marker position={route[0]}>
              <Popup>Start: {fromLocation}</Popup>
            </Marker>
          )}
          {route && route.length > 1 && (
            <Marker position={route[route.length - 1]}>
              <Popup>End: {toLocation}</Popup>
            </Marker>
          )}
          {route && route.length > 1 && (
            <Polyline positions={route} color="#4CAF50" weight={6} />
          )}
        </MapContainer>
      </div>
    </div>
  );
};

export default RoutePlanning;
