import React, { useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import L from "leaflet";
import "./RoutePlanning.css";

// Leaflet icon fix
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png"),
});

async function loadHazards() {
  const response = await fetch("/hazards.json");
  if (!response.ok) throw new Error("Failed to load hazards data");
  return await response.json();
}

function getDistanceMeters(lat1, lon1, lat2, lon2) {
  const toRad = (x) => (x * Math.PI) / 180;
  const R = 6371000;
  const dLat = toRad(lat2 - lat1);
  const dLon = toRad(lon2 - lon1);
  const a =
    Math.sin(dLat / 2) * Math.sin(dLat / 2) +
    Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) *
    Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
}

function checkHazardsNearRoute(routeCoords, hazards) {
  const result = [];
  hazards.forEach((hazard) => {
    for (const [lat, lon] of routeCoords) {
      if (getDistanceMeters(lat, lon, hazard.lat, hazard.lon) <= hazard.radius_meters) {
        result.push(hazard);
        break;
      }
    }
  });
  return result;
}

export default function RoutePlanning() {
  const [fromLocation, setFromLocation] = useState("");
  const [toLocation, setToLocation] = useState("");
  const [routeCoords, setRouteCoords] = useState(null);
  const [routeInfo, setRouteInfo] = useState(null);
  const [hazards, setHazards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [routeFound, setRouteFound] = useState(false);

  async function getCoordinates(place) {
    const response = await fetch(
      `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
        place
      )}&limit=1`
    );
    const data = await response.json();
    if (data && data.length > 0) {
      return [parseFloat(data[0].lat), parseFloat(data[0].lon)];
    }
    throw new Error("Location not found: " + place);
  }

  async function getRoute(startCoords, endCoords) {
    const response = await fetch(
      `https://router.project-osrm.org/route/v1/driving/${startCoords[1]},${startCoords[0]};${endCoords[1]},${endCoords[0]}?overview=full&geometries=geojson`
    );
    const data = await response.json();
    if (data.routes && data.routes.length > 0) {
      const route = data.routes[0];
      return {
        coordinates: route.geometry.coordinates.map(([lng, lat]) => [
          lat,
          lng,
        ]),
        distance: route.distance / 1000, // in km
        duration: route.duration / 60,   // in min
      };
    }
    throw new Error("Route calculation failed");
  }

  async function handleFindRoute() {
    if (!fromLocation || !toLocation) {
      alert("Please enter both source and destination.");
      return;
    }
    setLoading(true);
    setRouteCoords(null);
    setRouteInfo(null);
    setHazards([]);
    setRouteFound(false);

    try {
      const fromCoords = await getCoordinates(fromLocation.trim());
      const toCoords = await getCoordinates(toLocation.trim());
      const route = await getRoute(fromCoords, toCoords);

      setRouteCoords(route.coordinates);
      setRouteInfo(route);

      const hazardsData = await loadHazards();
      const hazardsOnRoute = checkHazardsNearRoute(route.coordinates, hazardsData);
      setHazards(hazardsOnRoute);

      setRouteFound(true);
    } catch (err) {
      alert("Error: " + err.message);
    }
    setLoading(false);
  }

  function handleStartNavigation() {
    if (routeCoords && routeCoords.length >= 2) {
      const start = routeCoords[0];
      const end = routeCoords[routeCoords.length - 1];
      const url = `https://www.google.com/maps/dir/?api=1&origin=${start[0]},${start[1]}&destination=${end[0]},${end[1]}`;
      window.open(url, "_blank");
    }
  }

  return (
    <div className="route-planning-container">
      <div className="planning-panel">
        <h1>SafePath AI</h1>
        <div className="route-form">
          <h2>üó∫Ô∏è Route Planning</h2>
          <div className="input-group">
            <label>From</label>
            <input
              type="text"
              value={fromLocation}
              onChange={(e) => setFromLocation(e.target.value)}
              placeholder="Enter starting location"
              className="location-input"
            />
          </div>
          <div className="input-group">
            <label>To</label>
            <input
              type="text"
              value={toLocation}
              onChange={(e) => setToLocation(e.target.value)}
              placeholder="Enter destination"
              className="location-input"
            />
          </div>
          <button
            onClick={handleFindRoute}
            disabled={loading}
            className="find-route-btn"
          >
            {loading ? "Finding Safe Route..." : "Find Safe Route"}
          </button>

          {routeFound && routeInfo && (
            <>
              <div className="route-info">
                <h3>‚úÖ Safe Route Found</h3>
                <div className="stats">
                  <div>
                    <strong>Distance:</strong> {routeInfo.distance.toFixed(2)} km
                  </div>
                  <div>
                    <strong>Estimated Time:</strong> {Math.floor(routeInfo.duration / 60)}h{" "}
                    {Math.round(routeInfo.duration % 60)}m
                  </div>
                </div>
              </div>
              <div className="route-warnings">
                <h4>‚ö†Ô∏è Safety Alerts</h4>
                {hazards.length === 0 && (
                  <div className="alert success">
                    ‚úÖ No major hazards detected. Safe route.
                  </div>
                )}
                {hazards.map((hazard) => (
                  <div
                    key={hazard.id}
                    className={`alert ${hazard.type}`}
                    title={hazard.description}
                  >
                    <span>{hazard.icon || "‚ö†Ô∏è"}</span>{" "}
                    {hazard.description} ({hazard.name})
                  </div>
                ))}
              </div>
              <button className="start-navigation-btn" onClick={handleStartNavigation}>
                üöó Start Navigation
              </button>
            </>
          )}
        </div>
      </div>
      <div className="map-container">
        <MapContainer
          center={routeCoords ? routeCoords[0] : [12.835, 79.703]}
          zoom={9}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
          {routeCoords && (
            <>
              <Marker position={routeCoords[0]}>
                <Popup>Start: {fromLocation}</Popup>
              </Marker>
              <Marker position={routeCoords[routeCoords.length - 1]}>
                <Popup>End: {toLocation}</Popup>
              </Marker>
              <Polyline positions={routeCoords} color="green" />
            </>
          )}
        </MapContainer>
      </div>
    </div>
  );
}
