import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import './RoutePlanning.css';

// Fix for leaflet icons
import L from 'leaflet';
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const RoutePlanning = () => {
  const [fromLocation, setFromLocation] = useState('');
  const [toLocation, setToLocation] = useState('');
  const [route, setRoute] = useState(null);
  const [routeData, setRouteData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [routeFound, setRouteFound] = useState(false);

  // Simple geocoding function
  const getCoordinates = async (location) => {
    const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${location}&limit=1`);
    const data = await response.json();
    if (data[0]) {
      return [parseFloat(data[0].lat), parseFloat(data[0].lon)];
    }
    throw new Error(`Location not found: ${location}`);
  };

  // Simple routing function
  const getRoute = async (start, end) => {
    try {
      const response = await fetch(`https://router.project-osrm.org/route/v1/driving/${start[1]},${start[0]};${end[1]},${end[0]}?overview=full&geometries=geojson`);
      const data = await response.json();
      if (data.routes?.[0]) {
        const route = data.routes[0];
        return {
          coordinates: route.geometry.coordinates.map(c => [c[1], c[0]]),
          distance: Math.round(route.distance / 100) / 10, // Convert to km
          duration: Math.round(route.duration / 60), // Convert to minutes
        };
      }
    } catch (error) {
      // Fallback: straight line
      const distance = Math.round(Math.sqrt(Math.pow((start[0] - end[0]) * 111, 2) + Math.pow((start[1] - end[1]) * 111, 2)) * 10) / 10;
      return {
        coordinates: [start, end],
        distance: distance,
        duration: Math.round(distance / 60 * 60),
      };
    }
  };

  const handleFindRoute = async () => {
    if (!fromLocation || !toLocation) {
      alert('Please enter both locations');
      return;
    }

    setIsLoading(true);
    setRouteFound(false);
    
    try {
      console.log('Finding route...');
      const fromCoords = await getCoordinates(fromLocation);
      const toCoords = await getCoordinates(toLocation);
      
      console.log('From:', fromCoords, 'To:', toCoords);
      
      const routeInfo = await getRoute(fromCoords, toCoords);
      
      console.log('Route:', routeInfo);
      
      setRoute(routeInfo.coordinates);
      setRouteData(routeInfo);
      setRouteFound(true);
      
    } catch (error) {
      alert('Error: ' + error.message);
    }
    setIsLoading(false);
  };

  return (
    <div className="route-planning-container">
      <div className="planning-panel">
        <div className="header">
          <h1>SafePath AI</h1>
        </div>
        <div className="route-form">
          <h2>üó∫Ô∏è Route Planning</h2>
          
          <div className="input-group">
            <label>From</label>
            <input
              type="text"
              value={fromLocation}
              onChange={(e) => setFromLocation(e.target.value)}
              placeholder="Enter starting location"
              className="location-input"
            />
          </div>

          <div className="input-group">
            <label>To</label>
            <input
              type="text"
              value={toLocation}
              onChange={(e) => setToLocation(e.target.value)}
              placeholder="Enter destination"
              className="location-input"
            />
          </div>

          <button onClick={handleFindRoute} disabled={isLoading} className="find-route-btn">
            {isLoading ? 'Finding Route...' : 'üõ°Ô∏è Find Safe Route'}
          </button>

          {routeFound && routeData && (
            <div className="route-info">
              <div className="route-stats">
                <h3>‚úÖ Safe Route Found</h3>
                <div className="stats">
                  <div className="stat-item">
                    <span className="label">Distance:</span>
                    <span className="value">{routeData.distance} km</span>
                  </div>
                  <div className="stat-item">
                    <span className="label">Estimated Time:</span>
                    <span className="value">{Math.floor(routeData.duration / 60)}h {routeData.duration % 60}m</span>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      <div className="map-panel">
        <MapContainer center={[13.0827, 80.2707]} zoom={8} style={{ height: "100%", width: "100%" }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; OpenStreetMap contributors'
          />
          
          {route && route[0] && (
            <Marker position={route[0]}>
              <Popup>Start: {fromLocation}</Popup>
            </Marker>
          )}
          
          {route && route[route.length - 1] && (
            <Marker position={route[route.length - 1]}>
              <Popup>End: {toLocation}</Popup>
            </Marker>
          )}
          
          {route && <Polyline positions={route} color="#4CAF50" weight={6} />}
        </MapContainer>
      </div>
    </div>
  );
};

export default RoutePlanning;
